package com.Day1;
import java.util.*;
public class Program2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int n;
        Scanner s = new Scanner(System.in);
        n=s.nextInt();
        if(n%2==0) {
        	System.out.println(n + "is even");
        }
        else {
        	System.out.println(n + "is odd");
        }
	}

}
